%% Open the text file.
fileID = fopen(filename,'r');

%% Read columns of data according to format string.
% This call is based on the structure of the file used to generate this
% code. If an error occurs for a different file, try regenerating the code
% from the Import Tool.
aline = fgetl(fileID);
flag = 1;
%while ~isempty(aline) && (flag ==1)

while (flag==1)
    
    if ~isempty(aline)
        
        text = textscan(aline, '%s');
        text = text{1};
%         if ~isempty(find(ismember(text,'Serial'),1)) % get serial No.
%             serialNo = text{5};
%         end
%         
%         if ~isempty(find(ismember(text,'MfgCode'),1)) % get tool size
%             MfgCodeText = textscan(aline,'%s');
%             MfgCodeText = MfgCodeText{1};
%             iq = ~cellfun('isempty', strfind(MfgCodeText,'MRT'));
%             toolsize =  MfgCodeText{find(iq,1)}(4);
%         end
%         
        if ~isempty(find(ismember(text,'TIME'),1)) % get all channel data
            allchannels = text;
            % read channel data
            
            fgetl(fileID); % skip unit
            fgetl(fileID); % skip format
            formatSpec='';
            for i=1:length(allchannels)
                formatSpec = strcat(formatSpec,'%f');
            end
            
            %textscan(fileID, '%[^\n\r]', startRow-1, 'ReturnOnError', false);
            dataArray = textscan(fileID, formatSpec, 'Delimiter', delimiter, 'MultipleDelimsAsOne', true, 'EmptyValue' ,NaN,'ReturnOnError', false);
            flag =0; % stop scanning
        end
    end
    aline = fgetl(fileID);
end
%% Close the text file.
fclose(fileID);


%% Post processing for unimportable data.
% No unimportable data rules were applied during the import, so no post
% processing code is included. To generate code which works for
% unimportable data, select unimportable cells in a file and regenerate the
% script.

%% Create output variable
data = [dataArray{1:end}];

% %% Remove channels not-used.
% idx=[];
% for i=1:length(Name)
% idx=[idx;find(not(cellfun('isempty',strfind(allchannels,Name{i}))))];
% end
% 
% selechannels=allchannels(idx);
% seledata=data(:,idx);
% 
% %% Determine the number of the elements in Echo
% 
% idx_ECR2=find(not(cellfun('isempty',strfind(selechannels,'ECR2'))));
% num_ECR2=length(idx_ECR2);
% NECH_ANT_value= zeros(3,1);
% if mod(num_ECR2-96, 100) == 0
%    NECH_ANT_value(1) = num_ECR2-96;
%    NECH_ANT_value(2) = 64;
%    NECH_ANT_value(3) = 32;
% elseif mod(num_ECR2-20,100) ==0
%    NECH_ANT_value(1) = num_ECR2-120;
%    NECH_ANT_value(2) = 60;
%    NECH_ANT_value(3) = 60;
% else
%    errordlg('The number of Echo elements is wrong', 'Error');
%    return
% end
%    


%% Clear temporary variables
clearvars filename delimiter startRow formatSpec fileID dataArray ans;

